					["custom"] = "local GetTime, GetSpellInfo, UnitCastingInfo, UnitChannelInfo = GetTime, GetSpellInfo, UnitCastingInfo, UnitChannelInfo\nlocal debuffName = GetSpellInfo(55360)\n\nlocal noClip = function(remDebuffTime)\n    local isCasting,_,_,_,_, endTimeMS = UnitCastingInfo(\"player\")\n    \n    if isCasting then\n        local remCastTime = (endTimeMS / 1000) - GetTime()\n        return remCastTime > remDebuffTime\n    end\n    \n    isCasting,_,_,_, startTimeMS, endTimeMS = UnitChannelInfo(\"player\")\n    \n    if isCasting then\n        local remCastTime = (endTimeMS / 1000) - GetTime()\n        return remCastTime > remDebuffTime\n    end\n    \n    -- GCD check\n    local s, d = GetSpellCooldown(61304)\n    if s == 0 then return end\n    return s + d - GetTime() > remDebuffTime\n    \nend\n\naura_env.OnEvent = function(trigger)\n    local lastTrigger = GetTime()\n    local isDebuff,_,_,_,_, dur, expTime = WA_GetUnitDebuff(\"target\", debuffName, \"PLAYER|HARMFUL\")\n    \n    if trigger then\n        if not isDebuff then return true end\n        return noClip(expTime - lastTrigger)\n    else\n        if not isDebuff then return end\n        return not noClip(expTime - lastTrigger)\n    end\nend",
