					["custom"] = "local tconcat, tinsert, tremove, tsort = table.concat, table.insert, table.remove, table.sort\nlocal AURAS = {}\nlocal AURAS_CACHE = {}\nlocal __AURAS_IDS = {48942, 54043, 19746, 48943, 48945, 48947, 32223}\nfor i, spellID in ipairs(__AURAS_IDS) do\n    local name, _, icon = GetSpellInfo(spellID)\n    AURAS[name] = {icon=icon, index=i}\nend\n\nlocal function get_caster_guid(unit, aura_name)\n    if not aura_name then return end\n    local caster = select(8, UnitBuff(unit, aura_name))\n    return caster and UnitGUID(caster)\nend\n\nlocal function tremovevalue(t, value)\n    for k, v in pairs(t) do\n        if v == value then\n            return tremove(t, k)\n        end\n    end\nend\n\nlocal function modify_state(allstates, unit, func)\n    local guid = UnitGUID(unit)\n    local aura_name = AURAS_CACHE[guid]\n    local state = allstates[aura_name]\n    if not state then return end\n    local name = UnitName(unit)\n    local rem = func(state.active, name)\n    tsort(state.active)\n    state.users = tconcat(state.active, \", \")\n    state.len = #state.active\n    state.changed = true\nend\n\nlocal function aura_check(allstates, unit)\n    local guid = UnitGUID(unit)\n    for aura_name in pairs(AURAS) do\n        if get_caster_guid(unit, aura_name) == guid then\n            AURAS_CACHE[guid] = aura_name\n            modify_state(allstates, unit, tinsert)\n            return true\n        end\n    end\nend\n\naura_env.init = function(allstates)\n    AURAS_CACHE = {}\n    for aura_name, buff_data in pairs(AURAS) do\n        local state = allstates[aura_name]\n        if not state then\n            state = {show = true, icon = buff_data.icon, index = buff_data.index}\n            allstates[aura_name] = state\n        end\n        state.active = {}\n        state.len = 0\n        state.users = \"\"\n        state.changed = true\n    end\n    for unit in WA_IterateGroupMembers() do\n        local _, class = UnitClass(unit)\n        if class == \"PALADIN\" then\n            aura_check(allstates, unit)\n        end\n    end\n    return true\nend\n\naura_env.aura_check = function(allstates, unit)\n    local guid = UnitGUID(unit)\n    local last_aura_name = AURAS_CACHE[guid]\n    if get_caster_guid(unit, last_aura_name) == guid then return end\n    \n    modify_state(allstates, unit, tremovevalue)\n    AURAS_CACHE[guid] = nil\n    aura_check(allstates, unit)\n    return true\nend",
