					["custom"] = "local LDW_Widget = aura_env\n\nLDW_Widget.locName = GetSpellInfo(71289)\nLDW_Widget.loaded = true\nLDW_Widget.equipmentSlots = LDW_Widget.equipmentSlots or {}\n--LDW_Widget.tab\n\nlocal classData = classData or {}\nlocal role = 1\n\nlocal function OnInit()\n  local myClass = select(2, UnitClass(\"player\"))\n  \n  -- 1 - Melee Attack, 2 - Range Attack, 3 - Caster\n  local specs = {\n    [\"DEATHKNIGHT\"] = {1, 1, 1},\n    [\"DRUID\"] = {3, 1, 3},\n    [\"HUNTER\"] = {2, 2, 2},\n    [\"MAGE\"] = {3, 3, 3},\n    [\"PALADIN\"] = {3, 1, 1},\n    [\"PRIEST\"] = {1, 1, 1},\n    [\"ROGUE\"] = {1, 1, 1},\n    [\"SHAMAN\"] = {3, 1, 3},\n    [\"WARLOCK\"] = {3, 3, 3},\n    [\"WARRIOR\"] = {1, 1, 1}\n  }\n  \n  classData = specs[myClass]\n  LDW_Widget.DefineEquipmentSlots()\nend\n\nlocal function SetSpec()\n  local tab, max = 1, 0\n  for i=1, 3 do\n    local _, _, pointsSpent = GetTalentTabInfo(i)\n    if pointsSpent > max then\n      tab = i\n      max = pointsSpent\n    end\n  end\n  return tab\nend\n\nfunction LDW_Widget.DefineEquipmentSlots()\n  local mySpec = SetSpec()\n  LDW_Widget.setName = string.format(\"LDW%d\", mySpec)\n  role = classData[mySpec]\n  if role == 1 then\n    LDW_Widget.equipmentSlots = { 16, 17 }\n  elseif role == 2 then\n    LDW_Widget.equipmentSlots = { 16, 17, 18 }\n  elseif role == 3 then\n    LDW_Widget.loaded = false\n  end\nend\n\nOnInit()",
