			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "local PhaseNotifications = aura_env\n\nlocal c = { -- constants\n    TRANS = \"TRANSITION\"\n}\n\nlocal function GetUnitCreatureId(unit)\n    local guid = UnitGUID(unit)\n    return (guid and tonumber(guid:sub(9, 12), 16)) or 0\nend\n\nlocal function GetBossUnitID(cid)\n    local UnitIDPool = { \"target\", \"focus\", \"mouseover\", \"boss1\", \"boss2\", \"boss3\", \"boss4\" }\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and GetUnitCreatureId(unitID) == cid then\n            return unitID\n        end\n    end\n    for i = 1, GetNumRaidMembers() do\n        local unitID = string.format(\"raid%dtarget\", i)\n        if GetUnitCreatureId(unitID) == cid then\n            return unitID\n        end\n    end\nend\n\nlocal function GetNPCHealth(cid)\n    local unitID = GetBossUnitID(cid)\n    if unitID then\n        return 100*UnitHealth(unitID)/UnitHealthMax(unitID)\n    else\n        return 0\n    end\nend\n\nlocal function GetLKPhase()\n    local health = GetNPCHealth(36597)\n    if health > 70 then\n        return 1\n    elseif health > 40 then\n        return 2\n    elseif health > 10 then\n        return 3\n    else\n        return 4\n    end\nend\n\nlocal function GetLKTransPhase()\n    local health = GetNPCHealth(36597)\n    if health > 40 then\n        return \"TRANSITION\", 1\n    elseif health > 10 then\n        return \"TRANSITION\", 2\n    end\nend\n\nlocal function GetPP_PhaseNM()\n    local health = GetNPCHealth(36678)\n    if health > 35 then\n        return 2\n    elseif health > 0 then\n        return 3\n    end\nend\n\nlocal phases = {\n    [\"SPELL_CAST_START\"] = {\n        [36678] = { -- Professor Putricide\n            [72851] = { c.TRANS, 1 }, \n            [72852] = { c.TRANS, 1 },\n            [73121] = { c.TRANS, 2 },\n            [73122] = { c.TRANS, 2 },\n        },\n        \n        [36597] = { -- The Lich King\n            [72262] = GetLKPhase,\n            [72350] = GetLKPhase,\n            [68981] = GetLKTransPhase, \n            [74270] = GetLKTransPhase, \n            [74271] = GetLKTransPhase, \n            [74272] = GetLKTransPhase,\n            [72259] = GetLKTransPhase, \n            [74273] = GetLKTransPhase, \n            [74274] = GetLKTransPhase, \n            [74275] = GetLKTransPhase\n        },\n    },\n    \n    [\"SPELL_CAST_SUCCESS\"] = {\n        [37955] = { -- Blood-Queen Lana'thel\n            [73070] = c.TRANS,\n        }\n    },\n    \n    [\"SPELL_AURA_REMOVED\"] = {\n        [36855] = { -- Lady Deathwhisper\n            [70842] = 2\n        }\n    },\n    \n    [\"SPELL_AURA_REMOVED\"] = {\n        [36678] = { -- Professor Putricide\n            [71615] = GetPP_PhaseNM, \n            [71618] = GetPP_PhaseNM\n        }\n    }\n}\n\nlocal L = GetLocale()\nPhaseNotifications.replicas = {\n    [\"enUS\"] = {\n        [\"Sindragosa\"] = {\n            sourceID = 36853,\n            Phase2 = \"Now, feel my master's limitless power and despair!\",\n            Air = \"Your incursion ends here! None shall survive!\",\n            AirDem = \"Rikk zilthuras rikk zila Aman adare tiriosh \",\n        },\n    },\n}\nPhaseNotifications.replicas = PhaseNotifications.replicas[L]\n\nfunction PhaseNotifications.AnnouncePhase(npcID, phase, transitionN)\n    WeakAuras.ScanEvents(\"MERFIN_BOSS_MODS\", \"BOSS_STATE\", npcID, \"PHASE\", phase, transitionN)\nend\n\nfunction PhaseNotifications.CheckPhase(subEvent, npcID, spellID)\n    if phases[subEvent]\n    and phases[subEvent][npcID]\n    and phases[subEvent][npcID][spellID] then\n        local phase = phases[subEvent][npcID][spellID]\n        if type(phase) == \"number\" then\n            PhaseNotifications.AnnouncePhase(npcID, phase)\n        elseif type(phase) == \"string\" then\n            PhaseNotifications.AnnouncePhase(npcID, phase)\n        elseif type(phase) == \"table\" then\n            PhaseNotifications.AnnouncePhase(npcID, phase[1], phase[2])\n        elseif type(phase) == \"function\" then\n            local bossPhase, transN = phase()\n            PhaseNotifications.AnnouncePhase(npcID, bossPhase, transN)\n        end\n    end\nend",
