				["init"] = {
					["custom"] = "local MerfinLib = {}\nlocal UnitIDPool = { \"target\", \"focus\", \"mouseover\", \"boss1\", \"boss2\", \"boss3\", \"boss4\" }\n\nlocal GetNumRaidMembers, GetNumPartyMembers, GetRaidRosterInfo = GetNumRaidMembers, GetNumPartyMembers, GetRaidRosterInfo\nlocal UnitExists, UnitGUID, UnitCastingInfo, UnitAffectingCombat = UnitExists, UnitGUID, UnitCastingInfo, UnitAffectingCombat\nlocal ipairs, pairs, tonumber, bband, stformat = ipairs, pairs, tonumber, bit.band, string.format\n\nMerfinLib.playerName = UnitName(\"player\")\n\nfunction MerfinLib.GetUnitCreatureId(unit, GUID)\n    local guid = GUID or unit and UnitGUID(unit)\n    return (guid and tonumber(guid:sub(9, 12), 16)) or 0\nend\n\nfunction MerfinLib.GetBossUnitID(cid)\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and MerfinLib.GetUnitCreatureId(unitID) == cid then\n            return unitID\n        end\n    end\n    for i = 1, GetNumRaidMembers() do\n        local unitID = stformat(\"raid%dtarget\", i)\n        if MerfinLib.GetUnitCreatureId(unitID) == cid then\n            return unitID\n        end\n    end\nend\n\nfunction MerfinLib.GetBossTarget(cid)\n    local UnitIDPool = { \"target\", \"focus\", \"mouseover\", \"boss1\" }\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and MerfinLib.GetUnitCreatureId(unitID) == cid then\n            local targetUnitID = string.format(\"%starget\", unitID)\n            return UnitName(targetUnitID)\n        end\n    end\n    for i = 1, GetNumRaidMembers() do\n        local unitID = string.format(\"raid%dtarget\", i)\n        if MerfinLib.GetUnitCreatureId(unitID) == cid then\n            return string.format(\"%starget\", unitID)\n        end\n    end\nend\n\nfunction MerfinLib.IsBossID(unitID, NpcIDPool, GUID)\n    local unitGUID = GUID or UnitGUID(unitID)\n    if unitGUID and (bband(unitGUID:sub(1, 5), 0x00F) == 3 or bband(unitGUID:sub(1, 5), 0x00F) == 5) then\n        local npcID = MerfinLib.GetUnitCreatureId(nil, unitGUID)\n        if npcID and NpcIDPool[npcID] then\n            return npcID\n        end\n    end\nend\n\nfunction MerfinLib.IsCertainBossActive(bossID)\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and UnitAffectingCombat(unitID) then\n            local npcID = MerfinLib.GetUnitCreatureId(unitID)\n            if npcID == bossID then\n                return true\n            end\n        end\n    end\n    for unit in WA_IterateGroupMembers() do\n        local targetUnitID = stformat(\"%starget\", unit)\n        if UnitExists(targetUnitID) and UnitAffectingCombat(targetUnitID) then\n            local npcID = MerfinLib.GetUnitCreatureId(targetUnitID)\n            if npcID == bossID then\n                return true\n            end\n        end\n    end\nend\n\nfunction MerfinLib.IsAnyBossActive(NpcIDPool)\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and UnitAffectingCombat(unitID) then\n            local npcID = MerfinLib.IsBossID(unitID, NpcIDPool)\n            if npcID then return npcID end\n        end\n    end\n    for unit in WA_IterateGroupMembers() do\n        local targetUnitID = stformat(\"%starget\", unit)\n        if UnitExists(targetUnitID) and UnitAffectingCombat(targetUnitID) then\n            local npcID = MerfinLib.IsBossID(targetUnitID, NpcIDPool)\n            if npcID then return npcID end\n        end\n    end\nend\n\nfunction MerfinLib.IsFightFinish(bossID)\n    if bossID and MerfinLib.IsCertainBossActive(bossID) then return end\n    local playerZone = GetRealZoneText()\n    local uId = ((GetNumRaidMembers() == 0) and \"party\") or \"raid\"\n    for raidIndex = 1, math.max(GetNumRaidMembers(), GetNumPartyMembers()) do\n        local unit = stformat(\"%s%d\", uId, raidIndex)\n        local name, _,_,_,_,_, zone, online, isDead = GetRaidRosterInfo(raidIndex)\n        if online and not isDead and playerZone == zone and UnitAffectingCombat(unit) then\n            return false\n        end\n    end\n    return true\nend\n\nfunction MerfinLib:ThrottleCheck(time)\n    self.throttle = self.throttle or 0\n    if GetTime() - self.throttle > time then\n        self.throttle = GetTime()\n        return true\n    end\nend\n\nfunction MerfinLib:StartTimer(allstates, duration, scheduled)\n    allstates[self.name] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = duration,\n        expirationTime = GetTime() + duration,\n        autoHide = true,\n    }\n    \n    if scheduled then\n        WeakAuras.timer:CancelTimer(self.timer)\n        self.timer = WeakAuras.timer:ScheduleTimer(\n            WeakAuras.ScanEvents, duration, \"MERFIN_BOSS_MODS\", \n        \"BOSS_STATE\", self.npcID, \"SCHEDULED_ACTION\", self.name)\n    end\n    \n    return true\nend\n\nfunction MerfinLib:StopTimer(allstates)\n    allstates[self.name] = {\n        show = false,\n        changed = true,\n    }\n    if self.timer then\n        WeakAuras.timer:CancelTimer(self.timer)\n    end\n    return true\nend\n\nfunction MerfinLib:ContainsSpellID(spellID)\n    if type(self.spellIDs) == \"table\" then\n        return tIndexOf(self.spellIDs, spellID)\n    elseif type(self.spellIDs) == \"number\" then\n        return self.spellIDs == spellID\n    end\nend\n\nfunction MerfinLib:DifficultyCheck(difficulty)\n    if type(difficulty) == \"table\" then\n        for _, diffStr in ipairs(difficulty) do\n            if diffStr == self.difficulty then\n                return true\n            end\n        end\n    elseif type(difficulty) == \"string\" then\n        if difficulty == \"ANY\" or difficulty == self.difficulty then\n            return true\n        end\n    end\nend\n\nfunction MerfinLib:IsNpcID(npcID, GUID)\n    local npcID = npcID or (GUID and tonumber(GUID:sub(9, 12), 16) or 0)\n    if type(self.npcID) == \"number\" then\n        return npcID == self.npcID\n    elseif type(self.npcID) == \"table\" then\n        return tIndexOf(self.npcID, npcID)\n    end\nend\n\nfunction MerfinLib:CooldownProcess(allstates, t, delay)\n    if not t then return end\n    local cooldown_info = t[self.difficulty] or t.ANY_DIFF or false\n    if cooldown_info then\n        local duration, scheduled = unpack(cooldown_info)\n        duration = delay and duration - delay or duration\n        if duration > 0 then\n            return self:StartTimer(allstates, duration, scheduled)\n        else\n            return self:StopTimer(allstates)\n        end\n    end\nend\n\nfunction MerfinLib:BOSS_STATE(allstates, npcID, subEvent, ...)\n    if not self:IsNpcID(npcID) then return end\n    if subEvent == \"FIGHT_DETECTION\" then\n        local fight, difficulty, delay = ...\n        if fight == \"START\" then\n            self.InFight, self.difficulty = true, difficulty\n            return self:CooldownProcess(allstates, self.cd[\"START\"], delay)\n        elseif fight == \"END\" then\n            self.InFight = false\n            return self:StopTimer(allstates)\n        end \n    elseif not self.InFight then return end\n    if subEvent == \"PHASE\" then\n        local phaseType, transN = ...\n        self.phaseType, self.transN = phaseType, transN\n        if self.cd[subEvent] and self.cd[subEvent][phaseType] then\n            local t = not transN and self.cd[subEvent][phaseType] or self.cd[subEvent][phaseType][transN]\n            return self:CooldownProcess(allstates, t)\n        end\n    elseif subEvent == \"SCHEDULED_ACTION\" then\n        local name = ...\n        if self.name == name then\n            return self:CooldownProcess(allstates, self.cd[subEvent])\n        end\n    end\nend\n\n-- Cast Object Functions \n\nfunction MerfinLib:StopCastTimer(allstates, GUID)\n    local stateName = string.format(\"%s%s\", GUID, self.mechanicName)\n    allstates[stateName] = {\n        show = false,\n        changed = true,\n    }\n    return true\nend\n\nfunction MerfinLib:StartCastTimer(allstates, GUID, duration)\n    local stateName = string.format(\"%s%s\", GUID, self.mechanicName)\n    allstates[stateName] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = duration,\n        expirationTime = GetTime() + duration,\n        autoHide = true,\n    }\n    return true\nend\n\nfunction MerfinLib.GetCastDuration(npcID)\n    local unitID = MerfinLib.GetBossUnitID(npcID)\n    if not unitID then return end\n    local name, _,_,_, startTime, endTime = UnitCastingInfo(unitID)\n    return name and (endTime - startTime) / 1000\nend\n\nfunction MerfinLib:OnCast(allstates, ...)\n    local subEvent, npcID, mechanicName = ...\n    if self.npcID ~= npcID then return end\n    if subEvent == \"BOSS_MECHANIC\" and self.mechanicName == mechanicName then\n        local _,_,_, srcGUID = ...\n        local duration = not self.notCast and MerfinLib.GetCastDuration(self.npcID) or self.default\n        return self:StartCastTimer(allstates, srcGUID, duration)\n    elseif subEvent == \"SPELL_INTERRUPT\" then\n        local destGUID = mechanicName\n        return self:StopCastTimer(allstates, destGUID)\n    end\nend\n\n-- Connection Functions\n\nlocal ListenerPool = {\n    [\"PULL_DETECTION\"] = { \"IsFightFinish\", \"IsAnyBossActive\", \"IsBossID\", \"IsFightFinish\" },\n    [\"COOLDOWN\"] = { \"IsNpcID\", \"StartTimer\", \"StopTimer\", \"CooldownProcess\", \"BOSS_STATE\", \"ThrottleCheck\" },\n    [\"CAST\"] = { \"StartCastTimer\", \"StopCastTimer\", \"GetCastDuration\", \"OnCast\" },\n    [\"TARGET_CHECK\"] = { \"GetBossTarget\", \"playerName\" }\n}\n\nfunction aura_env:Connect(listener, call)\n    local funcPool = ListenerPool[listener]\n    if not funcPool then return end\n    for _, funcName in ipairs(funcPool) do\n        self[funcName] = MerfinLib[funcName]\n    end\n    return true\nend\n\nfunction aura_env.SendConnection(delay)\n    WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, delay, \"MERFIN_BOSS_MODS\", \"CONNECT_LIB\", aura_env.Connect)\nend",
