					["custom"] = "function aura_env.IsSpellID(v, a1, a2, a3, a4, a5, a6, a7, a8)\n    return v == a1 or v == a2 or v == a3 or v == a4 or v == a5 or v == a6 or v == a7 or v == a8\nend\n\nlocal lightEssence = GetSpellInfo(67223)\nlocal darkEssence = GetSpellInfo(67177)\nlocal UnitDebuff = UnitDebuff\n\nlocal function GetDebuffs()\n    if WeakAuras.IsOptionsOpen() then\n        return 5, 8\n    end\n    local w, b = 0, 0\n    for unit in WA_IterateGroupMembers() do\n        if UnitDebuff(unit, lightEssence) then\n            w = w + 1\n        elseif UnitDebuff(unit, darkEssence) then\n            b = b + 1\n        end\n    end\n    return w, b\nend\n\nfunction aura_env.update(action)\n    local w, b = GetDebuffs()\n    if action == \"LIGHT_VORTEX\" then\n        if b > w then\n            return \"RAID: CHANGE -> LIGHT\\nSOAKERS: CHANGE -> |cff0000ffDARK|r + DEFS\"\n        elseif b < w then\n            return \"RAID: STAY LIGHT\\nSOAKERS: DEFS\"\n        end\n    elseif action == \"DARK_VORTEX\" then\n        if b > w then\n            return \"RAID: STAY |cff0000ffDARK|r\\nSOAKERS: DEFS\"\n        elseif b < w then\n            return \"RAID: CHANGE -> |cff0000ffDARK|r\\nSOAKERS: CHANGE -> LIGHT + DEFS\"\n        end\n    elseif action == \"SHIELD_LIGHT\" then\n        if b > w then\n            return \"RAID: STAY |cff0000ffDARK|r\\nSOAKERS: STAY LIGHT\"\n        elseif b < w then\n            return \"RAID: CHANGE -> |cff0000ffDARK|r \\nSOAKERS: CHANGE -> LIGHT\"\n        end\n    elseif action == \"SHIELD_DARK\" then\n        if b > w then\n            return \"RAID: CHANGE -> LIGHT\\nSOAKERS: CHANGE -> |cff0000ffDARK|r\"\n        elseif b < w then \n            return \"RAID: STAY LIGHT\\nSOAKERS: STAY |cff0000ffDARK|r\"\n        end \n    end\nend",
					["do_custom"] = true,
