						["subeventPrefix"] = "SPELL",
						["customName"] = "function()\n    return time()\nend",
						["custom"] = "function(event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, srcGUID, srcName,_,_,destName,_, spellID, spellName = ...\n        if subEvent == \"SPELL_CAST_START\" then\n            if aura_env.IsSpellID(spellID, 66046, 67206, 67207, 67208) then -- Light Vortex\n                aura_env.message = aura_env.update(\"LIGHT_VORTEX\")\n                return true\n            elseif aura_env.IsSpellID(spellID, 66058, 67182, 67183, 67184) then -- Dark Vortex\n                aura_env.message = aura_env.update(\"DARK_VORTEX\")\n                return true\n            end\n        elseif subEvent == \"SPELL_AURA_APPLIED\" then\n            if aura_env.IsSpellID(spellID, 65858, 67259, 67260, 67261) then -- Shield of Lights\n                aura_env.message = aura_env.update(\"SHIELD_LIGHT\")\n                return true\n            elseif aura_env.IsSpellID(spellID, 65874, 67256, 67257, 67258) then -- Shield of Darkness\n                aura_env.message = aura_env.update(\"SHIELD_DARK\")\n                return true\n            end\n        end\n    end\nend",
