			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "do\n    local replicas = {\n        [\"ruRU\"] = {\n            boss = \"'\",\n            pull = \"    !\",\n            transition = \"  !\",\n            emerge = \"  !\",\n            phase3 = \"  \"\n            \n        },\n        \n        [\"enUS\"] = {\n            boss = \"Anub'arak\",\n            pull = \"This place will serve as your tomb!\",\n            transition = \"burrows into the ground!\",\n            emerge = \"emerges from the ground!\",\n            phase3 = \"Leeching Swarm to heal himself\"\n        }\n        \n    }\n    \n    local L = GetLocale()\n    aura_env.replicas = replicas[L]\nend\n\n\n\nfunction aura_env.SetTimer(allstates, dur)\n    -- Cancel previous timer if there is some, just to be sure xd\n    WeakAuras.timer:CancelTimer(aura_env.timer)\n    aura_env.timer = WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, dur, \"SHADOW_STRIKE\", \"NEXT\")\n    allstates[\"ShadowStrikeCD\"] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = dur,\n        expirationTime = dur+GetTime(),\n        autoHide = true,\n    }\n    \n    return true\nend\n\nfunction aura_env.StopTimer(allstates)\n    WeakAuras.timer:CancelTimer(aura_env.timer)\n    allstates[\"ShadowStrikeCD\"] = {\n        show = false,\n        changed = true,\n    }\n    return true\nend\n\nfunction aura_env.IsFightFinish()\n    local playerZone = GetRealZoneText()\n    for raidIndex=1, GetNumRaidMembers() do \n        local unit = string.format(\"raid%d\", raidIndex)\n        local name,_,_,_,_,_, zone, online, isDead = GetRaidRosterInfo(raidIndex)\n        if online and not isDead and playerZone == zone and UnitAffectingCombat(unit) then\n            return false\n        end\n    end\n    return true\nend\n\n-- Server Check\nlocal serverName = GetRealmName()\nif serverName:find(\"WoW Circle\") then\n    aura_env.appendix = true\nend",
