						["customDuration"] = "function()\n    local duration = aura_env.duration\n    local expirationTime = duration + GetTime()\n    return duration, expirationTime\nend",
						["events"] = "CHAT_MSG_MONSTER_YELL, CHAT_MSG_RAID_BOSS_EMOTE, SHADOW_STRIKE, CLEU:UNIT_DIED:SPELL_CAST_START, PLAYER_REGEN_ENABLED",
						["custom"] = "function(allstates, event, ...)\n    if event == \"CHAT_MSG_MONSTER_YELL\" then\n        local text, srcName = ...\n        if srcName == aura_env.replicas.boss then\n            if text == aura_env.replicas.pull then\n                return aura_env.SetTimer(allstates, aura_env.config.pull)\n            end\n        end\n    elseif event == \"CHAT_MSG_RAID_BOSS_EMOTE\" then\n        local text = ...\n        if not text then return end\n        if text:find(aura_env.replicas.emerge) then\n            return aura_env.SetTimer(allstates, aura_env.config.emerge)\n        elseif text:find(aura_env.replicas.transition) then\n            return aura_env.StopTimer(allstates)\n        elseif aura_env.appendix and text:find(aura_env.replicas.phase3) then\n            local curTime = GetTime()\n            local expTime = allstates[\"ShadowStrikeCD\"] \n            and allstates[\"ShadowStrikeCD\"].expirationTime\n            if expTime and expTime > curTime then\n                local duration = expTime - curTime + 1.5\n                return aura_env.SetTimer(allstates, duration)\n            end\n        end\n    elseif event == \"SHADOW_STRIKE\" then\n        local subEvent = ...\n        \n        if subEvent == \"NEXT\" \n        and not aura_env.IsFightFinish() then\n            return aura_env.SetTimer(allstates, aura_env.config.between)\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _,_,_,_, destName, _,_, spellName = ...\n        if subEvent == \"UNIT_DIED\" then\n            if destName == aura_env.replicas.boss\n            or (UnitExists(destName) and aura_env.IsFightFinish()) then\n                return aura_env.StopTimer(allstates)\n            end\n        end\n    elseif (event == \"PLAYER_REGEN_ENABLED\" or event == \"OPTIONS\")\n    and aura_env.IsFightFinish() then\n        return aura_env.StopTimer(allstates)\n    end\nend",
