					["custom"] = "local SPELL_NAME = GetSpellInfo(73797) -- Soul Reaper\nlocal HARVEST_SOULS = GetSpellInfo(74297)\nlocal WINTER = GetSpellInfo(74272)\nlocal QUAKE = GetSpellInfo(72262)\nlocal FOF = GetSpellInfo(72350)\n\nlocal function update_state(allstates, duration, room)\n    local state = allstates[SPELL_NAME]\n    if not state then\n        state = {\n            show = true,\n            stacks = 0,\n            progressType = \"timed\",\n            name = SPELL_NAME,\n        }\n        allstates[SPELL_NAME] = state\n    end\n    state.changed = true\n    state.duration = duration\n    state.expirationTime = duration + GetTime()\n    if not room then state.stacks = state.stacks + 1 end\n    return true\nend\n\nlocal function reset(allstates)\n    allstates[SPELL_NAME] = {show = false, changed = true}\n    return true\nend\n\naura_env.cast_check = function(allstates, event, spell_name)\n    if event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        if spell_name == SPELL_NAME then\n            return update_state(allstates, 30)\n        elseif spell_name == HARVEST_SOULS then\n            return update_state(allstates, 55, true)\n        end\n    elseif event ~= \"UNIT_SPELLCAST_START\" then\n        return allstates[SPELL_NAME] and not UnitGUID(\"boss1\") and reset(allstates)\n    elseif spell_name == WINTER or spell_name == FOF then\n        return reset(allstates)\n    elseif spell_name == QUAKE then\n        local is_p2 = UnitHealth(\"boss1\") > UnitHealthMax(\"boss1\") * 0.4\n        return update_state(allstates, is_p2 and 40 or 66)\n    end\nend",
