				},
				["init"] = {
					["custom"] = "local SindyGPS = aura_env\n\nSindyGPS.myName = UnitName(\"player\")\nSindyGPS.lastBeacon = 0\nSindyGPS.beaconTargets = {}\nSindyGPS.widgetExpTime = 0\n--SindyGPS.threshold = N\n\nlocal soundsPath = \"Interface\\\\AddOns\\\\SharedMedia_MyMedia\\\\sound\\\\\"\nlocal soundFileNamePath = {\n    [\"LEFT\"] = string.format(\"%sGoToLeftSide.wav\", soundsPath),\n    [\"RIGHT\"] = string.format(\"%sGoToRightSide.wav\", soundsPath),\n    [\"MIDDLE\"] = string.format(\"%sGoToMiddleSide.wav\", soundsPath)\n}\n\nlocal function SendSideWhisper(unitName, sideName)\n    if SindyGPS.config.sendmessage then\n        if IsRaidLeader() then\n            local message = string.format(\"Your side is: %s\", sideName)\n            SendChatMessage(message, \"WHISPER\", nil, unitName)\n        end\n    end\nend\n\nlocal function AddState(allstates, sideName, sideString, isPlayer)\n    allstates[sideName] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\", \n        duration = 6,\n        expirationTime = SindyGPS.widgetExpTime,\n        autoHide = true,\n        sideName = sideName,\n        soundFileName = soundFileNamePath[sideName],\n        sideString = sideString,\n        isPlayer = isPlayer,\n        sound = SindyGPS.config.sound,\n        index = sideName == \"LEFT\" and 1 or sideName == \"MIDDLE\" and 2 or 3\n    }\nend\n\nlocal function AddSideUnit(sideName, finalString, name, isFirst)\n    local result = finalString\n    if not isFirst then\n        result = string.format(\"%s: %s\", sideName, name)\n    else\n        result = string.format(\"%s, %s\", result, name)\n    end\n    return result\nend\n\nlocal function SetSide(allstates, sideName, firstUnit, maxUnits)\n    local sideString = \"\"\n    local isFirst = false\n    local isPlayer = false\n    for i = firstUnit, firstUnit+maxUnits do\n        local unitName = SindyGPS.beaconTargets[i]\n        local formattedName = WA_ClassColorName(unitName)\n        sideString = AddSideUnit(sideName, sideString, formattedName, isFirst)\n        if not isFirst then\n            isFirst = true\n        end\n        if unitName == SindyGPS.myName then\n            isPlayer = true \n            WeakAuras.ScanEvents(\"SINDRAGOSA_GPS_SIDE\", sideName)\n            if SindyGPS.config.sideMsg then\n                SendChatMessage(sideName, \"SAY\")\n            end\n            if SindyGPS.config.sound then\n                PlaySoundFile(soundFileNamePath[sideName])\n            end\n        end\n        SendSideWhisper(unitName, sideName)\n    end\n    AddState(allstates, sideName, sideString, isPlayer)\nend\n\nfunction SindyGPS.SetStates(allstates, threshold)\n    SindyGPS.widgetExpTime = GetTime() + 6\n    if threshold == 2 then\n        SetSide(allstates, \"LEFT\", 1, 0)\n        SetSide(allstates, \"RIGHT\", 2, 0)\n    elseif threshold == 5 then\n        SetSide(allstates, \"LEFT\", 1, 1)\n        SetSide(allstates, \"MIDDLE\", 3, 0)\n        SetSide(allstates, \"RIGHT\", 4, 1)\n    elseif threshold == 6 then\n        SetSide(allstates, \"LEFT\", 1, 1)\n        SetSide(allstates, \"MIDDLE\", 3, 1)\n        SetSide(allstates, \"RIGHT\", 5, 1)\n    end\nend",
