						["unit"] = "player",
						["subeventSuffix"] = "_CAST_START",
						["custom"] = "function(allstates, event, ...)\n    local SindyGPS = aura_env\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent,_,_,_,_, destName, _, spellID = ...\n        if subEvent == \"SPELL_AURA_APPLIED\" and spellID == 70126 then\n            if GetTime() - SindyGPS.lastBeacon > 5 then\n                SindyGPS.beaconTargets = {}\n            end\n            SindyGPS.beaconTargets[#SindyGPS.beaconTargets + 1] = destName\n            SindyGPS.lastBeacon = GetTime()\n            if #SindyGPS.beaconTargets == SindyGPS.threshold then\n                SindyGPS.SetStates(allstates, SindyGPS.threshold)\n                return true\n            end\n        end\n    elseif event == \"PLAYER_ENTERING_WORLD\" then\n        local _,_,_,_, maxPlayers, dynamicDifficulty = GetInstanceInfo()\n        if maxPlayers == 10 then\n            SindyGPS.threshold = 2\n        elseif maxPlayers == 25 then\n            if dynamicDifficulty == 0 then\n                SindyGPS.threshold = 5\n            else\n                SindyGPS.threshold = 6\n            end\n        end\n        \n    elseif event == \"OPTIONS\" then\n        local sides = { \"LEFT\", \"MIDDLE\", \"RIGHT\" }\n        for _, stateName in ipairs(sides) do\n            allstates[stateName] = {\n                show = true,\n                changed = true,\n                progressType = \"timed\", \n                duration = 6,\n                expirationTime = GetTime() + 6,\n                autoHide = true,\n                sideString = string.format(\"%s: |cFF8788EEMerfin|r, |cFFFF7C0AProfanelka|r\", stateName),\n                index = stateName == \"LEFT\" and 1 or stateName == \"MIDDLE\" and 2 or 3\n            }\n        end\n        return true\n    end\nend",
