						["custom"] = "function(allstates, ...)\n    local event, _, combatEvent, sourceGUID, sourceName, _, destGUID, destName, _, spellId, _, _, _, stacks = ...\n    \n    \n    -- stop watching if not active\n    if not(aura_env.IsActive) then\n        local flag\n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n            flag = true\n        end\n        -- if flag then print(\"-- WA: stopped all states!\") end\n        return true\n    end\n    \n    \n    if (combatEvent == \"SPELL_AURA_APPLIED\" or combatEvent == \"SPELL_AURA_APPLIED_DOSE\" or combatEvent == \"SPELL_AURA_REFRESH\" or combatEvent == \"SPELL_AURA_REMOVED\") and (spellId == 68605) and (tonumber(string.sub(sourceGUID,9,12), 16) == 33109) and (tonumber(string.sub(destGUID,9,12), 16) == 33113) then\n        -- spell must be 68605 (blue pyrite dot)\n        -- source must be 33109 (salvaged demolisher)\n        -- dest must be 33113 (flame levi)\n        local key = \"PYRITESTACKS_\"..sourceGUID\n        local state = allstates[key] or {}\n        local energy, vehicleOwner\n        state.changed = true\n        state.show = true\n        if combatEvent == \"SPELL_AURA_REMOVED\" then \n            state.show = false\n            allstates[key] = state\n            return true \n        end\n        \n        -- check for vehicle owner name and energy\n        for raiderUnitId in WA_IterateGroupMembers() do\n            local petUnitId = string.gsub(raiderUnitId,\"raid\",\"raidpet\")\n            local petUnitGUID = UnitGUID(petUnitId) -- fetch guid\n            if UnitExists(petUnitId) and ( petUnitGUID == sourceGUID ) then\n                vehicleOwner = GetUnitName(raiderUnitId, true)\n                energy = UnitPower(petUnitId)\n                break\n            end\n        end\n        vehicleOwner = vehicleOwner or string.sub(sourceGUID, 13)\n        vehicleOwner = string.sub(vehicleOwner, 1, 6)\n        \n        if combatEvent == \"SPELL_AURA_REFRESH\" then\n            stacks = 10\n        else\n            stacks = stacks or 1\n        end\n        energy = energy or 1\n        \n        state.name = string.format(\"[%s] %s (%s)\", tostring(stacks), vehicleOwner, tostring(energy) )\n        -- print(\"-- AURA\", state.name) --DEBUG\n        state.progressType = \"timed\"\n        state.duration = 10\n        state.expirationTime = 10 + GetTime()\n        state.autoHide = true\n        allstates[key] = state\n        return true\n        \n        -- update energy number when casting pyrite or getting energized with fresh pyrite\n    elseif (combatEvent == \"SPELL_CAST_SUCCESS\" and spellId == 62490 and (tonumber(string.sub(sourceGUID,9,12), 16) == 33109) ) or (combatEvent == \"SPELL_ENERGIZE\" and spellId == 62473 and (tonumber(string.sub(destGUID,9,12), 16) == 33109) ) then\n        -- for SPELL_CAST_SUCCESS the SOURCE must be 33109 (salvaged demolisher)\n        -- for SPELL_ENERGIZE the DEST must be 33109 (salvaged demolisher)\n        local key = combatEvent == \"SPELL_CAST_SUCCESS\" and sourceGUID or destGUID\n        local state = allstates[key]\n        -- only do something, if that state exists and is shown\n        if state and state.show then\n            local stacksName = string.match(state.name, \"(%b[]%s[%x%a]+)%s(%b())\" )\n            if not stacksName then return end -- stop if we cannot read it\n            \n            -- check for vehicle owner name and energy\n            local energy\n            for raiderUnitId in WA_IterateGroupMembers() do\n                local petUnitId = string.gsub(raiderUnitId,\"raid\",\"raidpet\")\n                local petUnitGUID = UnitGUID(petUnitId) -- fetch guid\n                if UnitExists(petUnitId) and ( petUnitGUID == sourceGUID ) then\n                    energy = UnitPower(petUnitId)\n                    break\n                end\n            end\n            if not energy then return end -- stop if we cannot get the energy value\n            \n            state.changed = true\n            state.name = string.format(\"%s (%s)\", stacksName, tostring(energy) )\n            -- print(\"-- ENERGY\", state.name) --DEBUG\n            allstates[key] = state\n            return true\n        end\n    end\nend",
