					["custom"] = "local stformat, GetSpellInfo  = string.format, GetSpellInfo\nlocal UnitExists, UnitGUID = UnitExists, UnitGUID\nlocal UnitBuff, UnitDebuff = UnitBuff, UnitDebuff\nlocal corruption = GetSpellInfo(25368)\nlocal myGUID = UnitGUID(\"player\")\nlocal corUnits = {}\n\nlocal UnitIDPool = { \"target\", \"focus\", \"mouseover\", \"boss1\", \"boss2\", \"boss3\", \"boss4\" }\n\nlocal validAuras = {\n  [17800] = true, -- Shadow Mastery (Warlock)\n  [22959] = true, -- Impoved Scorch (Mage)\n  [57658] = true, -- % Crit (Shaman)\n  [54499] = true, -- % Crit (Ret)\n  [57933] = true, -- Tricks of the Trade (Rogue)\n  [70840] = true, -- Devious Mind (t10)\n  [29232] = true, -- Loatheb\n  [39089] = true, -- Thaddius\n  [29659] = true, -- Thaddius\n  [29660] = true, -- Thaddius\n  [39092] = true, -- Thaddius\n  [55849] = true, -- Spark Debuff (Malygos)\n  [61975] = true, -- Rune (Iron Council)\n  [64321] = true, -- Mashroom (Freya)\n  [63277] = true, -- Shadow Crash (Vezax)\n  [70867] = true, -- Essence (BQL)\n  [70879] = true, -- Essence (BQL)\n  [71473] = true, -- Essence (BQL)\n  [71525] = true, -- Essence (BQL)\n  [71530] = true, -- Essence (BQL)\n  [71531] = true, -- Essence (BQL)\n  [71532] = true, -- Essence (BQL)\n  [71533] = true, -- Essence (BQL)\n}\n\nlocal GetHP = function(unitId)\n  if unitId then\n    return 100 * UnitHealth(unitId) / UnitHealthMax(unitId)\n  end\nend\n\nlocal GetUnitId = function(unitGUID)\n  for _, unitId in ipairs(UnitIDPool) do\n    if UnitExists(unitId) and unitGUID == UnitGUID(unitId) then\n      return unitId\n    end\n  end\n  \n  for i = 1, GetNumRaidMembers() do\n    local unitId = stformat(\"raid%dtarget\", i)\n    if unitGUID == UnitGUID(unitId) then\n      return unitID\n    end\n  end\nend\n\nlocal GetDebuffInfo = function()\n  local isDebuff,_,_,_,_, dur, exp = WA_GetUnitDebuff(\"target\", corruption, 'PLAYER|HARMFUL')\n  return isDebuff, dur, exp\nend\n\nlocal GetMultipliers = function(unitId)\n  \n  local totalDamage = 0\n  local totalCrit = GetSpellCritChance(6)\n  \n  -- Player Buffs\n  for i = 1, 255 do\n    local _,_,_, count,_,_,_,_,_,_, spellId = UnitBuff('player', i)\n    \n    if not spellId then break end\n    \n    -- Buff: Shadow Weaving\n    if spellId == 15258 then\n      totalDamage = totalDamage + 2 * count\n      -- Buff: Tricks of the Trade    \n    elseif spellId == 57933 then\n      totalDamage = totalDamage + 15\n      -- Buff: Devious Mind    \n    elseif spellId == 70840 then\n      totalDamage = totalDamage + 10\n      -- Ulduar: Freya\n    elseif spellId == 64321 then\n      totalDamage = totalDamage + 50\n    end\n  end\n  \n  -- player Debuffs\n  for i = 1, 255 do\n    local _,_,_, count,_,_,_,_,_,_, spellId = UnitBuff('player', i)\n    if not spellId then break end\n    \n    -- Naxxramas: Thaddius\n    if spellId == 39089 or spellId == 29659 or spellId == 29660 or spellId == 39092 then\n      totalDamage = totalDamage + 10 * count\n      -- Naxxramas: Loatheb\n    elseif spellId == 29232 then\n      totalCrit = totalCrit + 50\n      -- EoE: Malygos\n    elseif spellId == 55849 then\n      totalDamage = totalDamage + 50\n      -- Ulduar: Council\n    elseif spellId == 61975 or spellId == 64320 then\n      totalDamage = totalDamage + 50\n      -- Ulduar: Vezax\n    elseif spellId == 63277 then\n      totalDamage = totalDamage + 100\n      -- ICC: BQL\n    elseif spellId == 70867 or spellId == 70879 or spellId == 71473 or spellId == 71525 \n    or spellId == 71530 or spellId == 71531 or spellId == 71532 or spellId == 71533 then\n      totalDamage = totalDamage + 100\n    end\n  end\n  \n  local isMagicalCrit = false\n  \n  -- unitId Debuffs\n  if unitId then\n    for i = 1, 255 do\n      local _,_,_, count,_,_,_,_,_,_, spellId = UnitDebuff(unitId, i)\n      if not spellId then break end\n      \n      -- Debuff: Shadow Mastery\n      if not isMagicalCrit and (spellId == 17800 or spellId == 22959) then\n        isMagicalCrit = true\n        totalCrit = totalCrit + 5\n        -- Debuff: Crit Percent\n        --elseif spellId == 54499 then\n        --    totalCrit = totalCrit + 3\n      end\n    end\n  end\n  \n  return totalCrit, totalDamage\nend\n\n\n-------------\n-- Main Logic\n-------------\n\nlocal RemoveGUID = function(destGUID)\n  corUnits[destGUID] = nil\nend\n\nlocal AddGUID = function(destGUID)\n  local unitId = GetUnitId(destGUID)\n  local crit, damage = GetMultipliers(unitId)\n  \n  corUnits[destGUID] = {\n    crit = crit,\n    damage = damage,\n  }\nend\n\nlocal WipeState = function(allstates)\n  if allstates[\"target\"] then\n    allstates[\"target\"] = {\n      show = false,\n      changed = true\n    }\n    return true\n  end\nend\n\nlocal BuildState = function(allstates, crit, damage, dur, exp)\n  allstates[\"target\"] = {\n    show = true,\n    changed = true,\n    autoHide = true,\n    progressType = \"timed\",\n    duration = dur,\n    expirationTime = exp,\n    crit = crit,\n    damage = damage,\n  }\n  return true\nend\n\nlocal GetState = function(allstates)\n  \n  if not UnitExists(\"target\") then \n    return WipeState(allstates)\n  end\n  \n  local isDebuff, dur, exp = GetDebuffInfo()\n  if not isDebuff then\n    return WipeState(allstates)\n  end\n  \n  local targetGUID  = UnitGUID(\"target\")\n  \n  local oldCrit = corUnits[targetGUID] and corUnits[targetGUID].crit or 0\n  local oldDamage = corUnits[targetGUID] and corUnits[targetGUID].damage or 0\n  \n  local newCrit, newDamage = GetMultipliers(\"target\")\n  \n  local crit, damage = newCrit - oldCrit, newDamage - oldDamage\n  \n  if allstates[\"target\"]\n  and crit == allstates[\"target\"].crit \n  and damage == allstates[\"target\"].damage then\n    return false\n  end\n  \n  BuildState(allstates, crit, damage, dur, exp)\n  return true\n  \nend\n\naura_env.OnCLEU = function(allstates, ...)\n  \n  local _, subEvent, srcGUID,_,_, destGUID,_,_, spellId, spellName = ...\n  \n  if subEvent == \"SPELL_CAST_SUCCESS\" then\n    -- Corruption manual apply\n    if srcGUID == myGUID and spellName == corruption then\n      AddGUID(destGUID)\n      BuildState(allstates, 0, 0)\n      return true\n    end\n    \n  elseif subEvent == \"SPELL_AURA_REMOVED\" then\n    \n    if srcGUID == myGUID and spellName == corruption then\n      if corUnits[destGUID] then \n        RemoveGUID(destGUID)\n      end\n      \n      if UnitExists(\"target\") and UnitGUID(\"target\") == destGUID then\n        return WipeState(allstates)\n      end\n      \n    elseif (UnitGUID(\"target\") == destGUID  or myGUID == destGUID) and validAuras[spellId] then\n      return GetState(allstates)\n    end\n    \n  elseif subEvent == \"SPELL_AURA_APPLIED\" then\n    \n    -- Corruption first apply\n    if srcGUID == myGUID and spellName == corruption and UnitGUID(\"target\") == destGUID then\n      return GetState(allstates)\n    end\n    \n    if (UnitGUID(\"target\") == destGUID or myGUID == destGUID) and validAuras[spellId] then\n      return GetState(allstates)\n    end\n    \n  elseif subEvent == \"SPELL_AURA_REFRESH\" then\n    -- Corruption duration refresh\n    if srcGUID == myGUID and spellName == corruption and UnitGUID(\"target\") == destGUID then\n      local _, dur, exp = GetDebuffInfo()\n      if allstates[\"target\"] then\n        allstates[\"target\"].duration = dur\n        allstates[\"target\"].expirationTime = exp\n        allstates[\"target\"].changed = true\n        return true\n      end\n    end\n  end\n  \nend\n\naura_env.OnPDDM = function(allstates)\n  return GetState(allstates)\nend\n\naura_env.OnPTC = function(allstates)\n  return GetState(allstates, dur, exp)\nend\n\naura_env.GetFormattedElements = function()\n  local crit = aura_env.state.crit or 0\n  local damage = aura_env.state.damage or 0\n  \n  local fCrit = string.format(\"|cFF%s%s%.1f%%|r\", crit > 0 and 'ffff00' or crit < 0 and 'ff4040' or '9d9d9d', crit > 0 and '+' or '', crit)\n  local fDamage = string.format(\"|cFF%s%s%.1f%%|r\", damage > 0 and 'ffff00' or damage < 0 and 'ff4040' or '9d9d9d', damage > 0 and '+' or '', damage)\n  \n  return fCrit, fDamage\nend",
