					["custom"] = "local function get_name(key)\n    local unit = aura_env.config[key] or \"player\"\n    local unit_name = UnitName(unit)\n    if not unit_name or unit_name == \"\" then\n        unit_name = UnitName(\"player\")\n    end\n    return unit_name\nend\n\nlocal LOOT_COLLECTOR_NAME = get_name(\"main\")\nlocal SHARDS_COLLECTOR_NAME = get_name(\"shards\")\nprint(\"|c069420FF[WeakAuras ~ML_LOOT_ALL]|r Loot:\", LOOT_COLLECTOR_NAME)\nprint(\"|c069420FF[WeakAuras ~ML_LOOT_ALL]|r Shards:\", SHARDS_COLLECTOR_NAME)\n\nlocal function get_masterloot_n()\n    local _loot, _shards\n    for n = 1, 40 do\n        local candidate_name = GetMasterLootCandidate(n)\n        if candidate_name == LOOT_COLLECTOR_NAME then\n            _loot = n\n        end\n        if candidate_name == SHARDS_COLLECTOR_NAME then\n            _shards = n\n        end\n    end\n    return _loot, _shards\nend\n\nlocal function is_legendary(loot_slot)\n    local _, _, _, quality = GetLootSlotInfo(loot_slot)\n    return quality == 5\nend\n\naura_env.loot_all = function()\n    if not GetLootSlotInfo(1) then return end\n    \n    local _loot, _shards = get_masterloot_n()\n    \n    if not _loot and not _shards then\n        print(\"Player collecting loot not found!\")\n        return\n    end\n    \n    for loot_slot = 1, GetNumLootItems() do\n        local player_n = is_legendary(loot_slot) and _shards or _loot\n        GiveMasterLoot(loot_slot, player_n)\n    end\nend",
