					["custom"] = "-- The aura coding is provided by Merfin\n\nlocal ValkyrsGrabWidget = aura_env\nlocal UnitInVehicle, UnitName, pairs, GetTime = UnitInVehicle, UnitName, pairs, GetTime\nlocal scanTime = 0.1\nValkyrsGrabWidget.mark = 2\nValkyrsGrabWidget.lastSummon = 0\n--ValkyrsGrabWidget.EnteredTimer\n--ValkyrsGrabWidget.ExitedTimer\n--ValkyrsGrabWidget.lastSummon\n\nfunction ValkyrsGrabWidget.StartScanEntered()\n    ValkyrsGrabWidget.EnteredTimer = WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, scanTime, \"VALKYRS_GRABBED_WIDGET\", \"SCAN_ENTERED\")\nend\n\nfunction ValkyrsGrabWidget.StartScanExited()\n    ValkyrsGrabWidget.ExitedTimer = WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, scanTime, \"VALKYRS_GRABBED_WIDGET\", \"SCAN_EXITED\")\nend\n\nfunction ValkyrsGrabWidget.CreateFrame(allstates, unit)\n    local unitName = UnitName(unit)\n    allstates[unitName] = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = 0,\n        expirationTime = 0,\n        autoHide = false,\n        name = WA_ClassColorName(unit),\n        icon = string.format(\"Interface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_%d\", ValkyrsGrabWidget.mark),\n        index = ValkyrsGrabWidget.mark,\n    }\n\n    ValkyrsGrabWidget.mark = ValkyrsGrabWidget.mark + 1\n    ValkyrsGrabWidget.StartScanExited()\n    return true\nend\n\nfunction ValkyrsGrabWidget.CreateTestFrames(allstates)\n    ValkyrsGrabWidget.mark = 2\n    local testNames = { \"|cFFF48CBAKyzny|r\", \"|cFFFFFFFFShoes|r\", \"|cFF8788EEMerfin|r\" }\n    for _, unitName in ipairs(testNames) do\n        allstates[unitName] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 0,\n            expirationTime = 0,\n            autoHide = false,\n            name = unitName,\n            icon = string.format(\"Interface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_%d\", ValkyrsGrabWidget.mark),\n            index = ValkyrsGrabWidget.mark,\n        }\n        ValkyrsGrabWidget.mark = ValkyrsGrabWidget.mark + 1\n    end\n    return true\nend\n\nfunction ValkyrsGrabWidget.OnScanExited(allstates)\n    local update, stopTimer = false, true\n    for unitName, state in pairs(allstates) do\n        if not UnitInVehicle(unitName) then\n            state.show = false\n            state.changed = true\n            update = true\n        elseif stopTimer then \n            stopTimer = false\n        end\n    end\n    if not stopTimer then\n        ValkyrsGrabWidget.StartScanExited()\n    end\n    return update\nend\n\nfunction ValkyrsGrabWidget.OnScanEntered(allstates)\n    if ( ValkyrsGrabWidget.mark == 5 ) or ( GetTime() - ValkyrsGrabWidget.lastSummon >= 10 ) then return end\n    local update = false\n    for unit in WA_IterateGroupMembers() do\n        if UnitInVehicle(unit) and not allstates[UnitName(unit)] then\n            update = ValkyrsGrabWidget.CreateFrame(allstates, unit)\n        end\n    end\n    ValkyrsGrabWidget.StartScanEntered()\n    return update\nend\n\nfunction ValkyrsGrabWidget.OnSummonValkyrs(allstates)\n    if GetTime() - ValkyrsGrabWidget.lastSummon < 10 then return end\n    local update = false\n    for _, state in pairs(allstates) do\n        update = true\n        state.show = false\n        state.changed = true\n    end\n    \n    ValkyrsGrabWidget.mark = 2\n    ValkyrsGrabWidget.lastSummon = GetTime()\n    ValkyrsGrabWidget.StartScanEntered()\n    return update\nend",
