			["actions"] = {
				["start"] = {
					["custom"] = "if not aura_env.config.features.clickMsg then \n    local button = aura_env.GetButtonFrame()\n    if button then\n        button:Hide()\n        button:ClearAllPoints()\n    end\n    return \nend\n\n--local region = WeakAuras.GetRegion(aura_env.id)\nlocal button = aura_env.GetButtonFrame()\nlocal state = aura_env.state\nlocal spellLink = GetSpellLink(state.spellId)\n\nbutton:SetAllPoints(aura_env.region)\nbutton:Show()\nbutton:RegisterForClicks(\"LeftButtonUp\", \"RightButtonUp\")\n\nbutton:SetScript(\"OnClick\", function(s, b)\n        \n        if b == \"LeftButton\" then\n            local msg = 'Use '..spellLink..' on '..WeakAuras.me..'!'\n            SendChatMessage(msg, \"WHISPER\", nil, state.srcName)\n            \n            local prefix = 'MR_CDS'\n            local addonMsg = WeakAuras.me..'-'..state.spellId\n            SendAddonMessage(prefix, addonMsg, \"WHISPER\", state.srcName)\n        else\n            local cd = state.expirationTime - GetTime()\n            local cdInfo = cd > 0 and aura_env.RoundTime(math.floor(cd)) or 'Ready!'\n            local msg = string.format('%s %s: %s', state.srcName, spellLink, cdInfo)\n            SendChatMessage(msg, UnitInRaid('player') and 'raid' or (UnitInParty('player') and 'party') or 'SAY')\n        end\nend)",
					["do_custom"] = true,
				},
				["init"] = {
					["custom"] = "local frontend = aura_env\n\nfunction frontend.OnInit()\n    WeakAuras.ScanEvents(\"MERFIN_RAID_CDS\", \"FRONTEND_REGISTER\", frontend)\nend\n\nfunction frontend.GetButtonFrame()\n    local frameName = aura_env.id .. aura_env.cloneId\n    local frame = _G[frameName]\n    if not frame then\n        frame = CreateFrame(\"Button\", frameName)\n        _G[frameName] = frame\n    end\n    return frame\nend\n\nfunction frontend.RoundTime(dec)\n    local ms = tonumber(dec)\n    return math.floor(ms / 60) .. \":\" .. (ms % 60)\nend",
					["do_custom"] = true,
				},
				["finish"] = {
					["custom"] = "local button = aura_env.GetButtonFrame()\n\nif button then\n    button:Hide()\n    button:ClearAllPoints()\nend",
					["do_custom"] = true,
				},
