 unit and UnitGUID(unit)\n    return (guid and tonumber(guid:sub(9, 12), 16)) or 0\nend\n\nlocal function GetUnitID(npcID)\n    for _, unitID in ipairs(UnitIDPool) do\n        if UnitExists(unitID) and GetUnitCreatureId(unitID) == npcID then\n            return unitID\n        end\n    end\n    for i = 1, GetNumRaidMembers() do\n        local unitID = stformat(\"raid%dtarget\", i)\n        if GetUnitCreatureId(unitID) == npcID then\n            return unitID\n        end\n    end\nend\n\nlocal function GetCastingTime(unitGUID, spellID)\n    local npcID = unitGUID and tonumber(unitGUID:sub(9, 12), 16) or 0\n    local unitID = GetUnitID(npcID)\n    if not unitID then return end\n    local name, _,_,_, startTime, endTime = UnitCastingInfo(unitID)\n    if not name then\n        name, _,_,_, startTime, endTime = UnitChannelInfo(unitID)\n    end\n    return name and (endTime - startTime) / 1000\nend\n\nfunction aura_env.ProcessBar(allstates, ...)\n    local _, subEvent, srcGUID, srcName,_, destGUID,_,_, spellID = ...\n    if subEvent == \"SPELL_INTERRUPT\" or subEvent == \"UNIT_DIED\" then\n        if allstates[destGUID] then\n            allstates[destGUID] = {\n                show = false,\n                changed = true\n            }\n            return true\n        end\n    elseif aura_env.mechanics[subEvent] and aura_env.mechanics[subEvent][spellID] then\n        local castingTime = GetCastingTime(srcGUID, spellID) or aura_env.mechanics[subEvent][spellID]\n        local spellName, _, icon = GetSpellInfo(spellID)\n        local stateName = srcGUID\n        allstates[stateName] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = castingTime,\n            expirationTime = castingTime + GetTime(),\n            autoHide = true,\n            icon = icon,\n            srcName = srcName or \"testName\",\n            spellName = spellName,\n        }\n        return true\n    end\nend",
