				},
				["init"] = {
					["custom"] = "local PullDetection = aura_env\nlocal combatStartTime = 0\nlocal currentBoss = 0\nlocal combatScan, bossScan\nlocal combatScanTime, bossScanTime = 3, 0.1\nlocal currentMap = GetCurrentMapAreaID()\n\nlocal stformat, GetTime, GetInstanceInfo = string.format, GetTime, GetInstanceInfo\n\n\nlocal NpcIDPool = {\n    [605] = {\n        [36612] = \"Lord Marrowgar\",\n        [36855] = \"Lady Deathwhisper\",\n        [37813] = \"Deathbringer Saurfang\",\n        [36626] = \"Festergut\",\n        [36627] = \"Rotface\",\n        [36678] = \"Professor Putricide\",\n        [37970] = \"Prince Valanar\",\n        [37972] = \"Prince Keleseth\",\n        [37973] = \"Prince Taldaram\",\n        [37955] = \"Blood-Queen Lana'thel\",\n        [36789] = \"Valithria Dreamwalker\",\n        [36853] = \"Sindragosa\",\n        [36597] = \"The Lich King\"\n    }\n}\n\nlocal function GetDifficultyFormat()\n    local _,_,_,_, maxPlayers, dynamicDifficulty = GetInstanceInfo()\n    return stformat(\"%d%s\", maxPlayers, dynamicDifficulty == 0 and \"normal\" or \"heroic\")\nend\n\n\nlocal function SendFightState(state, delay)\n    WeakAuras.ScanEvents(\"MERFIN_BOSS_MODS\", \"BOSS_STATE\", currentBoss, \"FIGHT_DETECTION\",  state, GetDifficultyFormat(), delay)\nend\n\nlocal function StartCombatScan()\n    combatScan = WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, combatScanTime, \"MERFIN_BOSS_MODS\", \"COMBAT_SCAN\")\nend\n\nlocal function StopCombatScan()\n    WeakAuras.timer:CancelTimer(combatScan)\nend\n\nlocal function StartBossScan()\n    bossScan = WeakAuras.timer:ScheduleTimer(WeakAuras.ScanEvents, bossScanTime, \"MERFIN_BOSS_MODS\", \"BOSS_SCAN\")\nend\n\nlocal function StopBossScan()\n    WeakAuras.timer:CancelTimer(bossScan)\nend\n\nlocal function StartCombat(delay)\n    StopBossScan()\n    SendFightState(\"START\", delay)\n    StartCombatScan()\nend\n\nlocal function StopCombat()\n    StopBossScan()\n    StopCombatScan()\n    SendFightState(\"END\")\n    currentBoss = 0\nend\n\nfunction PullDetection.OnUnitDead(unitGUID)\n    if currentBoss == PullDetection.IsBossID(nil, NpcIDPool[currentMap], unitGUID) then\n        StopCombat()\n    end\nend\n\nfunction PullDetection.OnBossScan()\n    local bossID = PullDetection.IsAnyBossActive(NpcIDPool[currentMap])\n    if bossID then\n        currentBoss = bossID\n        StartCombat(GetTime() - combatStartTime)\n        \n    elseif PullDetection.IsFightFinish() then\n        StopBossScan()\n    else\n        StartBossScan()\n    end\nend\n\nfunction PullDetection.OnCombatScan()\n    if PullDetection.IsFightFinish(currentBoss) then\n        StopCombat()\n    else\n        StartCombatScan()\n    end\nend\n\nfunction PullDetection.OnEnteringCombat()\n    if currentBoss == 0 then\n        combatStartTime = GetTime()\n        PullDetection.OnBossScan()\n    end\nend\n\nfunction PullDetection.OnEnteringWorld()\n    currentMap = GetCurrentMapAreaID()\n    if NpcIDPool[currentMap] then\n        PullDetection.initialized = true\n    else\n        PullDetection.initialized = false\n        if currentBoss ~= 0 then\n            StopCombat()\n        end\n    end\nend\n\nPullDetection.OnEnteringWorld()",
