					["custom"] = "local SPELL_NAME = GetSpellInfo(73781) -- Infest\nlocal WINTER = GetSpellInfo(74272)\nlocal QUAKE = GetSpellInfo(72262)\nlocal BOSS_GUID = \"0008EF5\"\n\nlocal function update_state(allstates, duration, stacks)\n  if UnitHealth(\"boss1\") < UnitHealthMax(\"boss1\") * 0.4 then return end\n  local state = allstates[SPELL_NAME]\n  if not state then\n    state = {\n      show = true,\n      stacks = 1,\n      progressType = \"timed\",\n      name = SPELL_NAME,\n    }\n    allstates[SPELL_NAME] = state\n  end\n  state.changed = true\n  state.duration = duration\n  state.expirationTime = duration + GetTime()\n  state.stacks = stacks or state.stacks + 1\n  return true\nend\n\nlocal function reset(allstates)\n  allstates[SPELL_NAME] = {show = false, changed = true}\n  return true\nend\n\naura_env.cast_check = function(allstates, event, spell_name)\n  if event == \"UNIT_SPELLCAST_START\" then\n    return spell_name == SPELL_NAME and update_state(allstates, 22)\n    or spell_name == WINTER and reset(allstates)\n    or spell_name == QUAKE and update_state(allstates, 12, 1)\n  elseif allstates[SPELL_NAME] then\n    return not UnitGUID(\"boss1\") and reset(allstates)\n  elseif UnitGUID(\"boss1\") then\n    return UnitGUID(\"boss1\"):sub(6, 12) == BOSS_GUID and update_state(allstates, 5, 1)\n  end\nend",
